            fill="none"
            stroke="cornflowerblue"
            stroke-width="0.1px"
            stroke-linejoin="round"
        />
    {/if}
</svg>

function crossfade({
    fallback,
    ...defaults
}: CrossfadeParams & {
    fallback?:
        | ((
                node: Element,
                params: CrossfadeParams,
                intro: boolean
          ) => TransitionConfig)
        | undefined;
}): [
    (
        node: any,
        params: CrossfadeParams & {
            key: any;
        }
    ) => () => TransitionConfig,
    (
        node: any,
        params: CrossfadeParams & {
            key: any;
        }
